import (
	fern "github.com/code-samples/fern"
	fernclient "github.com/code-samples/fern/client"
)

client := fernclient.NewClient()
response, err := client.Service.Hello(
	ctx,
	&fern.MyRequest{
		NumEvents: 5,
	},
)

