import (
	fern "github.com/imdb/fern"
	fernclient "github.com/imdb/fern/client"
	option "github.com/imdb/fern/option"
)

client := fernclient.NewClient(
	option.WithToken(
		"<YOUR_AUTH_TOKEN>",
	),
)
response, err := client.Imdb.CreateMovie(
	ctx,
	&fern.CreateMovieRequest{
		Title:  "string",
		Rating: 1.1,
	},
)

